list-view-config.query-select:

    	PREFIX vitro-public: <http://vitro.mannlib.cornell.edu/ns/vitro/public#>
    	PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>

		SELECT DISTINCT ?image ?file ?downloadLoc ?downloadUrl ?filename ?fileDescription ?mimeType
		WHERE
		{
		     ?subject ?property ?image .
		   	 OPTIONAL {
		     	?image <http://vivo.mydomain.edu/individual/hasFile> ?file .
		     	?file vitro-public:filename ?filename .
		     	?file vitro-public:mimeType ?mimeType .
		     	?file vitro-public:downloadLocation ?downloadLoc .
		     	?downloadLoc vitro-public:directDownloadUrl ?downloadUrl . 
		     }

		     OPTIONAL { ?file <http://vivo.mydomain.edu/individual/fileDescription> ?fileDescription  }		     
		}
